import PropTypes from 'prop-types';
import React from 'react';
import { connect } from 'react-redux';
import Header from './Header/Header';
import ProductSpace from './ProductsSpace/ProductSpace';
import Loading from './Loading';

import { getProducts } from '../reducers/action-creators';

class UserContainer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            products: true,
            user: false,
        };
    }

    componentDidMount() {

    }

    render() {
        return (
            <div style={{
                padding: '5px',
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%'
            }}>
                <Header />
                {this.props.isLoading ?
                    <Loading /> :
                    <ProductSpace productList={this.props.productList} />
                }
                <div>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        favouriteList: state.get('productList'),
        isLoading: state.getIn(['view', 'isLoading'])
    };
}

function mapDispatchToProps(dispatch) {
    return {
        getProducts: () => dispatch(getProducts())
    }
}

export const User = connect(mapStateToProps, mapDispatchToProps)(UserContainer);
