import React from 'react';
import Product from './Product';
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';
import AddIcon from '@material-ui/icons/Add';
import FlatButton from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import TextField from '@material-ui/core/TextField';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';
import Slide from '@material-ui/core/Slide';

function Transition(props) {
    return <Slide direction="up" {...props} />;
}

export default class ProductContainer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            newProduct: false,
            productDetail: false,
            newProductDialog: {
                name: '',
                description: '',
            }
        };
    }

    validateDetails = () => {
        console.log('Checking if everything is writen correctly');
        console.log(this.state.newProductDialog.name);
    };

    newProductStateChange = () => {
        
    }

    openNewDialog = () => {
        this.setState({ newProduct: true });
    };

    closeNewDialog = () => {
        this.setState({ newProduct: false });
    };

    render() {
        const { productList } = this.props;

        return (
            <div className={'row'}>
                {productList.map(product => <Product id={product.id} {...product}/>)}

                <Fab style={{
                        margin: 0,
                        top: 'auto',
                        right: 20,
                        bottom: 20,
                        left: 'auto',
                        position: 'fixed',
                        color: '#000000',
                        backgroundColor: '#F24F4F'
                    }} onClick={this.openNewDialog}>
                    <AddIcon />
                </Fab>

                <Dialog
                    open={this.state.newProduct}
                    TransitionComponent={Transition}
                    keepMounted
                    onClose={this.closeNewDialog}
                    aria-labelledby="alert-dialog-slide-title"
                    aria-describedby="alert-dialog-slide-description"
                >
                    <DialogTitle id="alert-dialog-slide-title">
                        {"Dodaj nowy produkt"}
                    </DialogTitle>
                    <DialogContent style={{
                        display: 'inline'
                    }}>
                        <form>
                            <TextField
                                autoFocus
                                id="new-product-name"
                                label="Nazwa"
                                value={this.state.newProductDialog.name}
                                helperText="Wpisz nazwe produktu"
                                margin="normal"
                                style={{
                                    width: '400px'
                                }}
                            >
                            </TextField>
                            <TextField
                                id="new-product-descr"
                                label="Opis"
                                value={this.state.newProductDialog.description}
                                helperText="Wpisz opis produktu"
                                margin="normal"
                                fullWidth
                                multiline
                            >
                            </TextField>
                        </form>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={this.validateDetails} color="primary">
                            Dodaj
                        </Button>
                        <Button onClick={this.closeNewDialog} color="primary">
                            Anuluj
                        </Button>
                    </DialogActions>
                </Dialog>
            </div>
        );
    }
}
