import React from 'react'
import { connect } from 'react-redux';

import {getShops, getCategories } from '../../reducers/action-creators';
//Material-UI components
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import Chip from '@material-ui/core/Chip';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
    PaperProps: {
        style: {
            maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
            width: 250,
        },
    },
};

function getStyles(name, that) {
    return {
        fontWeight:
            that.state.name.indexOf(name) === -1
                ? that.props.theme.typography.fontWeightRegular
                : that.props.theme.typography.fontWeightMedium,
    };
}

class FilterContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [],
            shops: []
        }
    }

    componentWillMount() {
        this.props.getShops();
        this.props.getCategories();
        this.setState({categories: this.props.categoryList});
        this.setState({shops: this.props.shopList})
    };

    handleChange = event => {
        this.setState({ name: event.target.value });
    };

    handleChangeMultiple = event => {
        const { options } = event.target;
        const value = [];
        for (let i = 0, l = options.length; i < l; i += 1) {
            if (options[i].selected) {
                value.push(options[i].value);
            }
        }
        this.setState({
            name: value,
        });
    };

    render() {
        const categories = this.props.categoryList;
        const shops = this.props.shopList;
        console.log(categories);
        console.log(shops);

        return (
            <div style={{
                padding: '5px',
                display: 'flex',
                flexWrap: 'wrap'
            }}>
                <FormControl style={{
                    minWidth: '120',
                    maxWidth: '300'
                }}>
                    <InputLabel htmlFor="select-multiple-chip">Kategorie</InputLabel>
                    <Select
                        multiple
                        value={this.state.categories}
                        onChange={this.handleChange}
                        input={<Input id="select-multiple-chip" />}
                        renderValue={selected => (
                            <div style={{
                                display: 'flex',
                                flexWrap: 'wrap',
                            }}>
                                {selected.map(value => (
                                    <Chip key={value} label={value} />
                                ))}
                            </div>
                        )}
                        MenuProps={MenuProps}
                    >
                        {categories.map(category => (
                            <MenuItem key={category.name} value={category.name} style={getStyles(category.name, this)}>
                                {category.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        shopList: state.getIn(['reducerShop', 'shopList']),
        categoryList: state.getIn(['reducerCategory', 'categoryList'])
    };
}

function mapDispatchToProps(dispatch) {
    return {
        getShops: () => dispatch(getShops()),
        getCategories: () => dispatch(getCategories())
    }
}

export const Filter = connect(mapStateToProps, mapDispatchToProps)(FilterContainer);
