import PropTypes from 'prop-types';
import React from 'react';
import { connect } from 'react-redux';
import Header from './Header/Header';
import { fromJS } from 'immutable'

import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';

import { getUser } from '../reducers/action-creators';

function TabContainer(props) {
    return (
        <Typography component="div" style={{ padding: 8 * 3 }}>
            {props.children}
        </Typography>
    );
}

TabContainer.propTypes = {
    children: PropTypes.node.isRequired,
};

class UserContainer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            value: 0,
            saveButton: false,
            modifyButton: true
        };
    }

    componentDidMount() {
        this.props.getUser();
    }

    handleChange = (event, value) => {
        this.setState({ value });
    };

    render() {
        const { classes } = this.props;
        const { value } = this.state;

        return (
            <div>
                <Header/>
                <AppBar style={{
                    position: 'static',
                    maxWidth: '800px',
                    margin: '0 auto',
                }}>
                    <Tabs value={value} onChange={this.handleChange}
                          fullWidth
                    >
                        <Tab label="Dane konta" />
                        <Tab label="Zmiana hasła" />
                    </Tabs>
                </AppBar>
                {value === 0 &&
                    <div style={{
                        alignContent: 'center',
                        alignItems: 'center',
                        maxWidth: '800px',
                        margin: '0 auto',
                        verticalAlign: 100
                    }}>
                        <form style={{
                            display: 'flex',
                            flexWrap: 'wrap',
                            margin: '0 auto'
                        }}
                        noValidate
                        autoComplete="off">
                            <TextField
                                disabled={this.state.modifyButton}
                                value={this.props.userData.name}
                                id="name-text"
                                label="Imię"
                                style={{
                                    marginTop: '19px',
                                    width: '400px',
                                    margin: '0 auto',
                                    padding: '10px'
                                }}
                            />
                            <TextField
                                disabled={this.state.modifyButton}
                                value={this.props.userData.surname}
                                id="surname-text"
                                label="Nazwisko"
                                style={{
                                    marginTop: '19px',
                                    width: '400px',
                                    margin: '0 auto',
                                    padding: '10px'
                                }}
                            />
                            <TextField
                                disabled={this.state.modifyButton}
                                value={this.props.userData.login}
                                id="login-text"
                                label="Login"
                                style={{
                                    marginTop: '19px',
                                    width: '400px',
                                    margin: '0 auto',
                                    padding: '10px'
                                }}
                            />
                            <TextField
                                disabled={this.state.modifyButton}
                                value={this.props.userData.email}
                                id="email-text"
                                label="Mail"
                                style={{
                                    marginTop: '19px',
                                    width: '400px',
                                    margin: '0 auto',
                                    padding: '10px'
                                }}
                            />
                        </form>
                    </div>
                }
                {value === 1 && <TabContainer>Item Two</TabContainer>}
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        userData: state.getIn(['reducerUser','user'])
    };
}

function mapDispatchToProps(dispatch) {
    return {
        getUser: () => dispatch(getUser())
    }
}

export const User = connect(mapStateToProps, mapDispatchToProps)(UserContainer);
