import React from 'react'
import { connect } from 'react-redux';

import {getShops, getCategories } from '../../reducers/action-creators';
//Material-UI components
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Chip from '@material-ui/core/Chip';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
    PaperProps: {
        style: {
            maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
            width: 250,
            margin: 'auto'
        },
    },
};

class FilterContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [],
            shops: [],
        };
    };

    componentWillMount() {
        this.props.getShops();
        this.props.getCategories();
        if(this.props.shopList.length > 0 && this.props.categoryList.length > 0) {
            this.props.shopList.forEach(shop => {
                if(!this.state.shops.includes(shop.name)) {
                    this.state.shops.push(shop.name);
                }
            });
            this.props.categoryList.forEach(category => {
                if(!this.state.categories.includes(category.name)) {
                    this.state.categories.push(category.name);
                }
            });
        }
    };

    handleChangeCategory = event => {
        this.setState({ categories: event.target.value });
        const categoryObjects = [];
        event.target.value.forEach(value => {
            console.log(value);
            this.props.categoryList.forEach(category => {
                if(category.name == value) {
                    console.log(`wkladam obiekt ${category}`);
                    categoryObjects.push(category);
                }
            })
        });
        this.props.filterCategory(categoryObjects);
    };

    handleChangeShop = event => {
        this.setState({shops: event.target.value});
        const shopObjects = [];
        event.target.value.forEach(value => {
            console.log(value);
            this.props.shopList.forEach(shop => {
                if(shop.name == value) {
                    console.log(`wkladam obiekt ${shop}`);
                    shopObjects.push(shop);
                }
            })
        });
        this.props.filterShop(shopObjects);
    };

    render() {

        return (
            <div style={{
                margin: '0 auto',
                padding: '10px'
            }}>
                <FormControl style={{
                    width: '200',
                    maxWidth: '300',
                    margin: '0 auto',
                    padding: '20px',
                    border: '3px',
                    borderColor: '#A59A9A',
                    marginLeft: 'auto',
                    marginRight: 'auto'
                }}>
                    <InputLabel htmlFor="select-multiple-chip">Kategorie</InputLabel>
                    <Select
                        multiple
                        value={this.state.categories}
                        onChange={this.handleChangeCategory}
                        input={<Input id="select-multiple-chip" />}
                        renderValue={selected => (
                            <div style={{
                                display: 'flex',
                                flexWrap: 'wrap',
                            }}>
                                {selected.map(value => (
                                    <Chip key={value} label={value} />
                                ))}
                            </div>
                        )}
                        style={{
                            minWidth: '200',
                            maxWidth: '300',
                            alignContent: 'center'
                        }}
                        MenuProps={MenuProps}
                    >
                        {this.props.categoryList.map(category => (
                            <MenuItem key={category.name} value={category.name} >
                                {category.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
                <FormControl style={{
                    width: '200',
                    maxWidth: '300',
                    margin: '0 auto',
                    padding: '20px',
                    border: '3px',
                    borderColor: '#A59A9A',
                    marginLeft: 'auto',
                    marginRight: 'auto'
                }}>
                    <InputLabel htmlFor="select-multiple-chip">Sklepy</InputLabel>
                    <Select
                        multiple
                        value={this.state.shops}
                        onChange={this.handleChangeShop}
                        input={<Input id="select-multiple-chip" />}
                        renderValue={selected => (
                            <div style={{
                                display: 'flex',
                                flexWrap: 'wrap',
                            }}>
                                {selected.map(value => (
                                    <Chip key={value} label={value} />
                                ))}
                            </div>
                        )}
                        MenuProps={MenuProps}
                    >
                        {this.props.shopList.map(shop => (
                            <MenuItem key={shop.name} value={shop.name} >
                                {shop.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        shopList: state.getIn(['reducerShop', 'shopList']),
        categoryList: state.getIn(['reducerCategory', 'categoryList'])
    };
}

function mapDispatchToProps(dispatch) {
    return {
        getShops: () => dispatch(getShops()),
        getCategories: () => dispatch(getCategories())
    }
}

export const Filter = connect(mapStateToProps, mapDispatchToProps)(FilterContainer);
