import {
    Map,
    List
} from 'immutable';

const INITIAL_STATE = Map({
    productList: List([]),
    view: Map({
        isLoading: false
    })
});

export default function (state = INITIAL_STATE, action) {
    switch (action.type) {
        case 'GET_PRODUCTS':
            return state.merge({
                view: {
                    isLoading: true
                }
            });
        case 'GET_PRODUCTS_SUCCESS':
            return state.merge({
                productList: action.payload,
                view: {
                    isLoading: false
                }
            });
        default: return state;
    }
}
