import {
    put,
    takeLatest
} from 'redux-saga/effects';
import {SERWER_LOCAL} from "../constants/AppConstants";

const getUserParams = (login, pass) => {
    try {
        console.log('fetching with get user');
        const response = fetch(`${SERWER_LOCAL}/api/product/modify`,{
            method: 'PUT',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                productId: product.productId,
                categoryId: product.categoryId,
                shopId: product.shopId,
                name: product.name,
                description: product.description,
                price: product.price,
                currency: product.currency,
                link: product.link
            })
        });
        console.log(response);
        console.log(response.body);
        return response.status;
    } catch(e) {
        console.log(`Could not fetch data from ${SERWER_LOCAL}.`);
    }
    const data = {
        userid: 1,
        name: 'Adam',
        surname: 'Nieadminowicz',
        login: 'loginek',
        password: 'haselko',
        email: 'xd@xd.pl',
        isAdmin: false
    };
    console.log(data);
    return data;
};

const addUser = (user) => {
    const resposne = {
        status: 'ok'
    };
    return resposne;
};

const modifyUser = (user) => {
    const resposne = {
        status: 'ok'
    };
    return resposne;
};

function* loadUser () {
    try {
        const userParams = yield getUserParams();
        yield put({ type: 'GET_USER_SUCCESS', payload: userParams});
    } catch (e) {
        yield put({type: 'GET_USER_FAILURE', payload: e});
    }
}

function* addUserFunction(user) {
    try {
        yield addUser(user);
        yield put({ type: 'ADD_USER_SUCCESS'});
    } catch (e) {
        yield put({ type: 'ADD_USER_FAILURE', payload: e});
    }
}

function* modifyUserFunction(user) {
    try {
        yield modifyUser(user);
        yield put({ type: 'ADD_USER_SUCCESS'});
    } catch (e) {
        yield put({ type: 'ADD_USER_FAILURE', payload: e});
    }
}

function* watchGetUser() {
    yield takeLatest('GET_USER', loadUser);
}

export default function* userSaga() {
    yield [
        watchGetUser()
    ]
}
